mapper.select:
SELECT f.question_id AS questionId, qq.question_text AS questionText, qs.name AS category, f.created_at AS createdAt FROM favorites f JOIN quiz_questions qq ON qq.question_id = f.question_id LEFT JOIN quiz_scenes qs ON qs.scene_id = qq.scene_id WHERE f.member_id = #{memberId}
AND qs.name = #{category}
ORDER BY f.created_at DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM favorites f
JOIN quiz_questions qq ON qq.question_id = f.question_id
LEFT JOIN quiz_scenes qs ON qs.scene_id = qq.scene_id
WHERE f.member_id = #{memberId}

    AND qs.name = #{category}
