mapper.select:
SELECT wa.question_id AS questionId, qq.question_text AS questionText, qs.name AS category, wa.wrong_count AS wrongCount, wa.last_wrong_at AS lastWrongAt FROM wrong_answers wa JOIN quiz_questions qq ON qq.question_id = wa.question_id LEFT JOIN quiz_scenes qs ON qs.scene_id = qq.scene_id WHERE wa.member_id = #{memberId}
AND COALESCE(wa.last_wrong_at, wa.created_at) >= #{fromDateTime}
AND qq.question_text LIKE CONCAT('%', #{keyword}, '%')
AND qs.name = #{category}
ORDER BY COALESCE(wa.last_wrong_at, wa.created_at) DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM wrong_answers wa
JOIN quiz_questions qq ON qq.question_id = wa.question_id
LEFT JOIN quiz_scenes qs ON qs.scene_id = qq.scene_id
WHERE wa.member_id = #{memberId}

    AND COALESCE(wa.last_wrong_at, wa.created_at) >= #{fromDateTime}


    AND qq.question_text LIKE CONCAT('%', #{keyword}, '%')


    AND qs.name = #{category}
